// 브라우저 언어 자동 감지
function detectBrowserLanguage() {
    const browserLang = navigator.language || navigator.userLanguage;
    
    if (browserLang.startsWith('ko')) return 'ko';
    if (browserLang.startsWith('ja')) return 'ja';
    if (browserLang.startsWith('es')) return 'es';
    if (browserLang.startsWith('zh')) return 'zh-TW';
    if (browserLang.startsWith('vi')) return 'vi';
    if (browserLang.startsWith('th')) return 'th';
    
    return 'en';
}

// IP 기반 국가 감지 (선택사항)
async function detectCountryByIP() {
    try {
        const response = await fetch('https://ipapi.co/json/');
        const data = await response.json();
        const countryCode = data.country_code;
        
        // 국가 코드를 언어 코드로 매핑
        const countryToLang = {
            'KR': 'ko',
            'JP': 'ja',
            'ES': 'es',
            'MX': 'es',
            'AR': 'es',
            'TW': 'zh-TW',
            'HK': 'zh-TW',
            'VN': 'vi',
            'TH': 'th'
        };
        
        return countryToLang[countryCode] || 'en';
    } catch (error) {
        console.log('IP detection failed, using browser language');
        return detectBrowserLanguage();
    }
}

// 현재 언어 초기화
let currentLang = localStorage.getItem('tikfind_lang') || detectBrowserLanguage();

// 로그인한 사용자의 언어 설정 로드
async function loadUserLanguage() {
    try {
        const response = await fetch('/api/current_user');
        const data = await response.json();
        
        if (data.success && data.user && data.user.preferredLanguage) {
            const userLang = data.user.preferredLanguage;
            if (userLang !== currentLang) {
                currentLang = userLang;
                localStorage.setItem('tikfind_lang', userLang);
                return true; // 언어가 변경됨
            }
        }
    } catch (error) {
        console.log('사용자 언어 설정 로드 실패:', error);
    }
    return false;
}

// 처음 방문 시 사용자 언어 또는 IP 기반 언어 감지
if (!localStorage.getItem('tikfind_lang')) {
    loadUserLanguage().then(changed => {
        if (changed) {
            location.reload();
        } else {
            detectCountryByIP().then(lang => {
                if (lang !== currentLang) {
                    currentLang = lang;
                    localStorage.setItem('tikfind_lang', lang);
                    location.reload();
                } else {
                    localStorage.setItem('tikfind_lang', lang);
                }
            });
        }
    });
} else {
    // 로그인한 사용자의 언어 설정 확인
    loadUserLanguage().then(changed => {
        if (changed) {
            location.reload();
        }
    });
}

// 번역 함수
function t(key) {
    return translations[currentLang]?.[key] || translations['en']?.[key] || key;
}

// 언어 변경
function changeLanguage(lang) {
    if (translations[lang]) {
        currentLang = lang;
        localStorage.setItem('tikfind_lang', lang);
        location.reload();
    }
}

// 번역 적용
function applyTranslations() {
    document.querySelectorAll('[data-i18n]').forEach(element => {
        const key = element.getAttribute('data-i18n');
        element.textContent = t(key);
    });
}

// 페이지 로드 시 번역 적용
document.addEventListener('DOMContentLoaded', function() {
    applyTranslations();
    createLanguageSelector();
});

// 언어 선택 드롭다운 생성
function createLanguageSelector() {
    const languages = {
        'en': 'English',
        'ko': '한국어',
        'ja': '日本語',
        'es': 'Español',
        'zh-TW': '繁體中文',
        'vi': 'Tiếng Việt',
        'th': 'ไทย'
    };
    
    // 페이지 내 language-selector가 있으면 생성
    const langSelector = document.getElementById('language-selector');
    if (langSelector) {
        let html = '<select id="lang-dropdown" class="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 bg-white">';
        
        for (const [code, name] of Object.entries(languages)) {
            const selected = code === currentLang ? 'selected' : '';
            html += `<option value="${code}" ${selected}>${name}</option>`;
        }
        
        html += '</select>';
        langSelector.innerHTML = html;
        
        document.getElementById('lang-dropdown').addEventListener('change', function(e) {
            changeLanguage(e.target.value);
        });
    }
    
    // 헤더의 language-selector가 있으면 생성
    const headerLangSelector = document.getElementById('header-language-selector');
    if (headerLangSelector) {
        const currentLangName = languages[currentLang] || 'English';
        const languageFlags = {
            'en': '🇺🇸',
            'ko': '🇰🇷',
            'ja': '🇯🇵',
            'es': '🇪🇸',
            'zh-TW': '🇹🇼',
            'vi': '🇻🇳',
            'th': '🇹🇭'
        };
        
        let html = `
            <div class="relative">
                <button id="lang-btn" class="flex items-center space-x-2 px-3 py-2 bg-slate-700 text-white rounded-lg hover:bg-slate-600 transition text-sm">
                    <span>${languageFlags[currentLang] || '🌐'}</span>
                    <span>${currentLangName}</span>
                    <i class="fas fa-chevron-down text-xs"></i>
                </button>
                <div id="lang-menu" class="hidden absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-50">
        `;
        
        for (const [code, name] of Object.entries(languages)) {
            const isActive = code === currentLang ? 'bg-indigo-50 text-indigo-600' : 'text-gray-700 hover:bg-gray-50';
            html += `
                <button class="lang-option w-full text-left px-4 py-2 ${isActive} transition flex items-center space-x-2" data-lang="${code}">
                    <span>${languageFlags[code] || '🌐'}</span>
                    <span>${name}</span>
                    ${code === currentLang ? '<i class="fas fa-check ml-auto text-indigo-600"></i>' : ''}
                </button>
            `;
        }
        
        html += `
                </div>
            </div>
        `;
        
        headerLangSelector.innerHTML = html;
        
        // 드롭다운 토글
        const langBtn = document.getElementById('lang-btn');
        const langMenu = document.getElementById('lang-menu');
        
        langBtn.addEventListener('click', function(e) {
            e.stopPropagation();
            langMenu.classList.toggle('hidden');
        });
        
        // 언어 선택
        document.querySelectorAll('.lang-option').forEach(option => {
            option.addEventListener('click', function() {
                const lang = this.getAttribute('data-lang');
                changeLanguage(lang);
            });
        });
        
        // 외부 클릭 시 메뉴 닫기
        document.addEventListener('click', function() {
            langMenu.classList.add('hidden');
        });
    }
}

// Export for use in other scripts
window.TikFindI18n = {
    t,
    changeLanguage,
    applyTranslations,
    createLanguageSelector,
    currentLang: () => currentLang
};
